import React from 'react';
import { Card, Image, Row, Col } from 'antd';
import { addressTranslator, nameTranslator } from '../../libs/utils';
import Section from './Section';
import SectionForArray from './SectionForArray';

const { Meta } = Card;
const serverURL = process.env.REACT_APP_SERVER_URL;

function DisplayDetails({ user }) {
  const contact = [
    { key: "1", prop: "Mobile Number", value: user?.contactNo, },
    { key: "2", prop: "Mail", value: user?.email, },
    { key: "3", prop: "Address", value: addressTranslator(user?.address), },
  ];
  const charges = [
    {
      key: "1", prop: 'Consultation', value: user?.fees?.consultation,
    }
  ];
  const clinic = [
    { key: "1", prop: "Phone", value: user?.clinic?.contactNo, },
    { key: "2", prop: "Mail", value: user?.clinic?.email, },
    { key: "3", prop: "Address", value: addressTranslator(user?.clinic?.address), },
  ];

  const urlGenerator = (serverURL, profileImage) => {
    console.log("Profile Image ", profileImage);
    const url = `${serverURL}${profileImage?.replace(/public/g, "")}`;
    console.log("url ", url);
    return url;
  }

  console.log(user?.profileImage)
  return <Card
    hoverable
    title={<h1>
      {nameTranslator(user)}
    </h1>}
    extra={`${user?.experience} years of experience`}
  >
    <Row>
      <Col span={8} push={2}>
        <Image
          width={200}
          height={200}
          preview={false}
          src={urlGenerator(serverURL, user?.profileImage)}
          fallback="data:image/png;base64,"
        />
      </Col>
      <Col span={8} push={6}>
        <Meta description={user?.description} />
      </Col>
    </Row>

    <Section
      label="Contact"
      dataSource={contact}
    />
    <Section
      label="Fees and Charges"
      dataSource={charges}
    />
    <SectionForArray
      title={"Professional"}
      labels={["Services", "Specializations", "Memberships", "Education", "Registrations", "Awards and Recognition", "Disease",]}
      values={[user?.services, user?.specializations, user?.memberships, user?.education, user?.registrations, user?.awardsAndRecognition, user?.disease,]}
      isArray={true}
    />
    {user?.clinic ?
      <Section
        label="Clinic"
        dataSource={clinic}
      /> :
      <></>}
  </Card>;
}

export default DisplayDetails;
